// App.js

import React, { useState, useEffect } from 'react';
// 1. IMPORTAÇÃO: Importamos o nosso novo componente.
import ProductCard from './ProductCard'; 
import logo from './logo.svg';
import './App.css';

function App() {
  const [produtos, setProdutos] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/produtos')
      .then(response => response.json())
      .then(data => setProdutos(data))
      .catch(error => console.error('Erro ao buscar produtos:', error));
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Produtos da Cida Semi Joias</h1>
      </header>

      {/* 2. ALTERAÇÃO: Criamos um container para os nossos produtos */}
      <div className="products-container">
        {/* Mapeamos a lista 'produtos' como antes... */}
        {produtos.map(produto => (
          // ...mas em vez de uma <li>, renderizamos o nosso componente ProductCard.
          // Passamos o 'produto' inteiro como uma "prop" para o componente filho.
          <ProductCard key={produto.id} produto={produto} />
        ))}
      </div>

    </div>
  );
}

export default App;
