// 1. Importamos o 'useState' e o 'useEffect' do React.
import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import './ProductCard.js';
import './ProductCard.css';

function App() {
  // 2. Criamos um "estado" para armazenar nossos produtos.
  // 'produtos' é a variável que guardará a lista.
  // 'setProdutos' é a função que usamos para atualizar essa lista.
  // Começa como uma lista vazia [].
  const [produtos, setProdutos] = useState([]);

  // 3. Usamos o useEffect para buscar os dados da API.
  // O array vazio no final, [], significa que este efeito
  // rodará apenas UMA VEZ, quando o componente for montado na tela.
  useEffect(() => {
    // 4. 'fetch' é a função do navegador para fazer requisições de rede.
    // Estamos pedindo os dados do nosso backend.
    fetch('http://localhost:3001/produtos')
      .then(response => response.json()) // Pegamos a resposta e a transformamos em JSON
      .then(data => setProdutos(data)) // Pegamos os dados e os guardamos no nosso estado
      .catch(error => console.error('Erro ao buscar produtos:', error)); // Tratamos possíveis erros
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Produtos da Cida Semi Joias</h1>

        {/* 5. Aqui, vamos exibir os produtos. */}
        {/* O método '.map()' percorre cada item da lista 'produtos' */}
        {/* e renderiza um elemento HTML para cada um. */}
        <ul>
          {produtos.map(produto => (
            // 'key' é um identificador único que o React precisa.
            <li key={produto.id}>
              {produto.nome} - R$ {produto.preco}
            </li>
          ))}
        </ul>

      </header>
    </div>
  );
}

export default App;
